export interface Patient {
    id: string;
    name: string;
    symptoms: string[];
    triageLevel: 1 | 2 | 3 | 4 | 5;
    status: 'waiting to be seen' | 'in appointment' | 'waiting on test results' | 'ready for discharge'
    registrationTime: Date;
    lastUpdated: Date;
    estimatedWaitTime: number;
}

export interface PatientUpdate {
    id: string;
    symptoms: string[];
}

export const TRIAGE_LEVELS = {
    1: "Immediate, life-threatening",
    2: "Emergent, high risk",
    3: "Urgent",
    4: "Less urgent",
    5: "Non-urgent"
} as const;

export const STATUS_MAP = {
    1: 'Waiting to be seen',
    2: 'In appointment',
    3: 'Waiting on test results',
    4: 'Ready for discharge'
}

export type TriageLevel = keyof typeof TRIAGE_LEVELS;
